  val passwordPattern = Pattern.compile("((?=.*[a-z])(?=.*\\d)(?=.*[A-Z]).{8,40})")
//  Be between 8 and 40 characters long
//  Contain at least one digit.
//    Contain at least one lower case character.
//    Contain at least one upper case character.

  override def validateUser(user: RegistrationUser): Future[Completed] = {

    def isMailValid() = buildValidated(mailPattern.matcher(user.mail).matches(), "Invalid mail address")
    def isPasswordValid() = buildValidated (passwordPattern.matcher(user.password).matches(), "Invalid password ")
    def isNameValid() = buildValidated (user.name.forall(namePattern.matcher(_).matches()), "Invalid name")
    def isSecondNameValid() = buildValidated (user.secondName.forall(namePattern.matcher(_).matches()), "Invalid secondName")

    validateLoginMail(user.login, user.mail).map { validatedLoginMail =>
      (validatedLoginMail, isMailValid(), isPasswordValid(), isNameValid(), isSecondNameValid()).mapN((_, _, _, _, _) => ()) match {
        case Valid(_) => Completed
        case Invalid(errors) => throw new ValidationException(errors.map(_ ++ "; ").reduceLeft(_ ++ _))
      }
    }
  }

  override def validateLoginMail(login: String, mail: String): Future[ValidationResult[Unit]] = {

    def existLogin(usersMaybe: Seq[User]): ValidationResult[Unit] =
      buildValidated(!usersMaybe.exists(_.login.equalsIgnoreCase(login)), "Such login is already exists")

    def existMail(usersMaybe: Seq[User]): ValidationResult[Unit] =
      buildValidated(!usersMaybe.exists(_.mail.equalsIgnoreCase(login)), "Such mail is already registered")

    userDao.getUsersByLoginOrMail(login, mail).map { existingUsers =>
      (existLogin(existingUsers), existMail(existingUsers)).mapN((_, _) => ())
    }
  }

  private def buildValidated(isValid: Boolean, errorMessage: String): ValidationResult[Unit] =
    if (isValid) ().validNel else errorMessage.invalidNel
}

object ValidationService {
  type ValidationResult[A] = ValidatedNel[String, A]
}


